import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';

const Header = () => {
  const location = useLocation();

  // Проверка активного пути для подсветки кнопки в меню
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <DirectionsCarIcon sx={{ mr: 2 }} />
        <Typography variant="h6" component={Link} to="/" sx={{ flexGrow: 1, color: 'white' }}>
          Автосалон
        </Typography>
        <Box>
          <Button 
            color="inherit" 
            component={Link} 
            to="/cars"
            sx={{ fontWeight: isActive('/cars') ? 'bold' : 'normal' }}
          >
            Автомобили
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/customers"
            sx={{ fontWeight: isActive('/customers') ? 'bold' : 'normal' }}
          >
            Покупатели
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/shops"
            sx={{ fontWeight: isActive('/shops') ? 'bold' : 'normal' }}
          >
            Магазины
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/calculator"
            sx={{ fontWeight: isActive('/calculator') ? 'bold' : 'normal' }}
          >
            Калькулятор
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
